import React from "react";
import ListGroup from "react-bootstrap/ListGroup";
import { Badge } from "react-bootstrap";

const TodoItem = (props) => {
	let itemHtml = (
		<>
			<span className="done">{props.todo.text}</span>
			<Badge className="float-end">
				&#128077;
			</Badge>
			<Badge className="float-end bg-danger mx-2" >Delete</Badge>

		</>
	);
	const deleteItem = (index)=>{
		let newList = delete prevlist[index]
		setList(newList);

}
	if (props.todo.done === false) {
		itemHtml = (
			<>
				<span className="done" >{props.todo.text}</span>
				<Badge className="float-end" onClick={() => props.markAsDone(props.todo.id)}>&#128078;</Badge>
				<Badge className="float-end bg-danger mx-2" onClick={deleteItem(props.todo.id)} >Delete</Badge>

			</>
		);
	}


	return <ListGroup.Item>{itemHtml}</ListGroup.Item>;
};

export default TodoItem;
