import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import ListGroup from "react-bootstrap/ListGroup";
import TodoItem from "./TodoItem";

const TodoList = () => {
  let initialList = [
    { id: 1, text: "Clean the office", done: true },
    { id: 2, text: "Clean the office", done: false },
    { id: 3, text: "Clean the office", done: false },
    { id: 4, text: "Clean the office", done: true },
  ];
  const [list, setList] = useState(initialList);
  const [textinput, setTextInput] = useState("");

  const TodoItems = list.map((item) => {
    return <TodoItem key={item.id} todo={item} />;
  });

  const handleTextInput = (e) => {
    setTextInput(e.target.value);
  };

  const addTodoItem = () => {
    let newTodo = {
      id: list[list.length - 1].id + 1,
      text: textinput,
      done: false,
    };

    setList((prevlist) => [...prevlist, newTodo]);
    setTextInput("");
  };

  const markAsDone = (id) => {
    const newList = list.map((item) => {
      if (item.id === id) {
        item.done = true;
      }
      return item;
    });

    setList(newList);
  };

  return (
    <Card>
      <Card.Header></Card.Header>
      <Card.Body>
        <ListGroup>{TodoItems}</ListGroup>
      </Card.Body>
      <Card.Footer>
        <input type="text" onChange={handleTextInput} />
        <Button variant="primary" className="float-end" onClick={addTodoItem}>
          Add
        </Button>
      </Card.Footer>
    </Card>
  );
};

export default TodoList;
